sys_variable_value.value:
(function execute(inputs, outputs) {
    if (!inputs.content)
        throw "Content is mandatory. Provide a valid content.";
    if (!inputs.name)
        throw "Attachment name is mandatory. Provide a valid attachment name.";
    try {
        var attachment = new GlideSysAttachment();
        var rec = inputs.event_rec;
        var fileName = inputs.name;
        var contentType = inputs.content_type;
        var base64Encodedcontent = inputs.content;
        var sys_id = attachment.writeBase64(rec, fileName, contentType, base64Encodedcontent);
        outputs.attachment_sys_id = sys_id;
        outputs.status = "Success";
    } catch (e) {
        outputs.status = "Error";
        outputs.error_message = 'Encountered an error. Check log for more details.';
        gs.error(e);
    }
})(inputs, outputs);

