sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == '200' && inputs.response) {

        var xmlDoc = new XMLDocument2();
        xmlDoc.parseXML(inputs.response);
        var responseStatus = xmlDoc.getNode(
            "/s:Envelope/s:Body/m:DeleteAttachmentResponse/m:ResponseMessages/m:DeleteAttachmentResponseMessage"
        ).getAttribute('ResponseClass');
        if (responseStatus === 'Success') {
            outputs.status = 'Success';
        } else
            outputs = new ExchangeErrorHandler().DeleteAttachment(
                inputs, outputs);
    } else
        outputs = new ExchangeErrorHandler().DeleteAttachment(
            inputs, outputs);
})(inputs, outputs);

