sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == '200' && inputs.response) {

        var xmlDoc = new XMLDocument2();
        xmlDoc.parseXML(inputs.response);
        var responseStatus = xmlDoc.getNode(
            "/s:Envelope/s:Body/m:GetAttachmentResponse/m:ResponseMessages/m:GetAttachmentResponseMessage"
        ).getAttribute('ResponseClass');
        if (responseStatus === 'Success') {
            outputs.status = 'Success';
            outputs.name = xmlDoc.getNodeText(
                "/s:Envelope/s:Body/m:GetAttachmentResponse/m:ResponseMessages/m:GetAttachmentResponseMessage/m:Attachments/t:FileAttachment/t:Name");
            outputs.content_type = xmlDoc.getNodeText(
                "/s:Envelope/s:Body/m:GetAttachmentResponse/m:ResponseMessages/m:GetAttachmentResponseMessage/m:Attachments/t:FileAttachment/t:ContentType");
			outputs.content = xmlDoc.getNodeText(
                "/s:Envelope/s:Body/m:GetAttachmentResponse/m:ResponseMessages/m:GetAttachmentResponseMessage/m:Attachments/t:FileAttachment/t:Content");
        } else
            outputs = new ExchangeErrorHandler().lookUpAttachment(
                inputs, outputs);
    } else
        outputs = new ExchangeErrorHandler().lookUpAttachment(
            inputs, outputs);
})(inputs, outputs);

